      border: 'border-emerald-800/50',
      text: 'text-emerald-50',
      textSecondary: 'text-emerald-300',
      accent: 'bg-emerald-500/25',
      badgeBg: 'bg-emerald-800/80',
      badgeBorder: 'border-emerald-700/40',
