      text: 'text-amber-50',
      textSecondary: 'text-amber-300',
      accent: 'bg-amber-500/25',
      badgeBg: 'bg-amber-800/80',
      badgeBorder: 'border-amber-700/40',
      borderL: 'border-l-amber-500'
    },
    morning: {
      bg: 'bg-sky-950/90',
      border: 'border-sky-800/50',
      text: 'text-sky-50',
      textSecondary: 'text-sky-300',
      accent: 'bg-sky-500/25',
      badgeBg: 'bg-sky-800/80',
      badgeBorder: 'border-sky-700/40',
      borderL: 'border-l-sky-500'
    },
    ocean: {
      bg: 'bg-cyan-950/90',
      border: 'border-cyan-800/50',
      text: 'text-cyan-50',
      textSecondary: 'text-cyan-300',
      accent: 'bg-cyan-500/25',
      badgeBg: 'bg-cyan-800/80',
      badgeBorder: 'border-cyan-700/40',
      borderL: 'border-l-cyan-500'
    },
    sunset: {
      bg: 'bg-orange-950/90',
      border: 'border-orange-800/50',
      text: 'text-orange-50',
      textSecondary: 'text-orange-300',
      accent: 'bg-orange-500/25',
      badgeBg: 'bg-orange-800/80',
      badgeBorder: 'border-orange-700/40',
      borderL: 'border-l-orange-500'
    },
    night: {
      bg: 'bg-slate-950/90',
      border: 'border-slate-700/50',
      text: 'text-slate-100',
      textSecondary: 'text-slate-400',
      accent: 'bg-slate-500/25',
      badgeBg: 'bg-slate-800/80',
      badgeBorder: 'border-slate-700/40',
      borderL: 'border-l-slate-500'
    }
  };
  return themeColors[theme] || themeColors.default;
};

// Theme-based color mapping for header and navbar – shades match theme (dark UI, light text)
const getThemeNavColors = (theme) => {
  const themeNavColors = {
    default: {
      headerText: 'text-indigo-100',
      headerSecondary: 'text-indigo-300',
      navActive: 'text-indigo-400',
      navActiveBg: 'bg-indigo-500/25',
      navInactive: 'text-indigo-300/80',
      navHover: 'hover:text-indigo-200',
      border: 'border-indigo-800/40',
      navBadgeBg: 'bg-indigo-800/80',
      navBadgeBorder: 'border-indigo-700/40'
    },
    forest: {
      headerText: 'text-emerald-100',
      headerSecondary: 'text-emerald-300',
      navActive: 'text-emerald-400',
      navActiveBg: 'bg-emerald-500/25',
      navInactive: 'text-emerald-300/80',
      navHover: 'hover:text-emerald-200',
      border: 'border-emerald-800/40',
      navBadgeBg: 'bg-emerald-800/80',
      navBadgeBorder: 'border-emerald-700/40'
    },
    sunshine: {
      headerText: 'text-amber-100',
      headerSecondary: 'text-amber-300',
      navActive: 'text-amber-400',
      navActiveBg: 'bg-amber-500/25',
      navInactive: 'text-amber-300/80',
      navHover: 'hover:text-amber-200',
      border: 'border-amber-800/40',
      navBadgeBg: 'bg-amber-800/80',
      navBadgeBorder: 'border-amber-700/40'
    },
    morning: {
      headerText: 'text-sky-100',
      headerSecondary: 'text-sky-300',
      navActive: 'text-sky-400',
      navActiveBg: 'bg-sky-500/25',
      navInactive: 'text-sky-300/80',
      navHover: 'hover:text-sky-200',
      border: 'border-sky-800/40',
      navBadgeBg: 'bg-sky-800/80',
      navBadgeBorder: 'border-sky-700/40'
    },
    ocean: {
      headerText: 'text-cyan-100',
      headerSecondary: 'text-cyan-300',
      navActive: 'text-cyan-400',
      navActiveBg: 'bg-cyan-500/25',
      navInactive: 'text-cyan-300/80',
      navHover: 'hover:text-cyan-200',
      border: 'border-cyan-800/40',
      navBadgeBg: 'bg-cyan-800/80',
      navBadgeBorder: 'border-cyan-700/40'
    },
    sunset: {
      headerText: 'text-orange-100',
      headerSecondary: 'text-orange-300',
      navActive: 'text-orange-400',
      navActiveBg: 'bg-orange-500/25',
      navInactive: 'text-orange-300/80',
      navHover: 'hover:text-orange-200',
      border: 'border-orange-800/40',
      navBadgeBg: 'bg-orange-800/80',
      navBadgeBorder: 'border-orange-700/40'
    },
    night: {
      headerText: 'text-slate-100',
      headerSecondary: 'text-slate-400',
      navActive: 'text-slate-300',
      navActiveBg: 'bg-slate-500/25',
      navInactive: 'text-slate-400',
      navHover: 'hover:text-slate-200',
      border: 'border-slate-700/40',
      navBadgeBg: 'bg-slate-800/80',
      navBadgeBorder: 'border-slate-700/40'
    }
  };
  return themeNavColors[theme] || themeNavColors.default;
};

// Theme accent RGB for PillNav hover animation (opacity applied separately) – match theme names
const THEME_ACCENT_RGB = {
  default: [129, 140, 248],   // indigo-400
  forest: [52, 211, 153],     // emerald-400
  sunshine: [251, 191, 36],   // amber-400
  morning: [56, 189, 248],    // sky-400
  ocean: [34, 211, 238],      // cyan-400
  sunset: [251, 146, 60],     // orange-400
  night: [148, 163, 184]      // slate-400
};

// Theme bg RGB for DarkVeil overlay & Card transparency (950-ish dark)
const THEME_BG_RGB = {
  default: [30, 27, 75],      // indigo-950
  forest: [2, 44, 34],        // emerald-950
  sunshine: [69, 26, 3],      // amber-950
  morning: [7, 37, 65],       // sky-950
  ocean: [8, 51, 68],         // cyan-950
  sunset: [67, 20, 7],        // orange-950
  night: [2, 6, 23]           // slate-950
};
const getThemeBgRgba = (theme, opacity = APP_CONFIG.CARD_TRANSPARENCY) => {
  const [r, g, b] = THEME_BG_RGB[theme] || THEME_BG_RGB.default;
  return `rgba(${r}, ${g}, ${b}, ${opacity})`;
};
const getThemeOverlayRgba = (theme, opacity = 0.2) => {
  const [r, g, b] = THEME_BG_RGB[theme] || THEME_BG_RGB.default;
  return `rgba(${r}, ${g}, ${b}, ${opacity})`;
};
const getThemeAccentRgba = (theme, opacity = 0.2) => {
  const [r, g, b] = THEME_ACCENT_RGB[theme] || THEME_ACCENT_RGB.default;
  return `rgba(${r}, ${g}, ${b}, ${opacity})`;
};

/* ================= GRADUAL BLUR COMPONENT ================= */

const DEFAULT_GRADUAL_BLUR_CONFIG = {
  position: 'bottom',
  strength: 2,
  height: '1.5rem',
  divCount: 5,
  exponential: true,
  zIndex: 1000,
  animated: true,
  duration: '0.3s',
  easing: 'ease-out',
  opacity: 1,
  curve: 'linear',
  responsive: true,
  target: 'parent',
  className: '',
  style: {}
};

const GRADUAL_BLUR_PRESETS = {
  top: { position: 'top', height: '6rem' },
  bottom: { position: 'bottom', height: '6rem' },
  left: { position: 'left', height: '6rem' },
  right: { position: 'right', height: '6rem' },
  subtle: { height: '4rem', strength: 1, opacity: 0.8, divCount: 3 },
  intense: { height: '10rem', strength: 4, divCount: 8, exponential: true },
  smooth: { height: '8rem', curve: 'bezier', divCount: 10 },
  sharp: { height: '5rem', curve: 'linear', divCount: 4 },
  header: { position: 'top', height: '8rem', curve: 'ease-out' },
  footer: { position: 'bottom', height: '8rem', curve: 'ease-out' },
  sidebar: { position: 'left', height: '6rem', strength: 2.5 },
  'page-header': { position: 'top', height: '10rem', target: 'page', strength: 3 },
  'page-footer': { position: 'bottom', height: '10rem', target: 'page', strength: 3 }
};

const GRADUAL_BLUR_CURVE_FUNCTIONS = {
  linear: p => p,
  bezier: p => p * p * (3 - 2 * p),
  'ease-in': p => p * p,
  'ease-out': p => 1 - Math.pow(1 - p, 2),
  'ease-in-out': p => (p < 0.5 ? 2 * p * p : 1 - Math.pow(-2 * p + 2, 2) / 2)
};

const mergeConfigs = (...configs) => configs.reduce((acc, c) => ({ ...acc, ...c }), {});

const getGradientDirection = position => {
  const directions = {
    top: 'to top',
    bottom: 'to bottom',
    left: 'to left',
    right: 'to right'
  };
  return directions[position] || 'to bottom';
};

const debounce = (fn, wait) => {
  let t;
  return (...a) => {
    clearTimeout(t);
    t = setTimeout(() => fn(...a), wait);
  };
};

const useResponsiveDimension = (responsive, config, key) => {
  const [val, setVal] = useState(config[key]);
  useEffect(() => {
    if (!responsive) return;
    const calc = () => {
      const w = window.innerWidth;
      let v = config[key];
      if (w <= 480 && config['mobile' + key[0].toUpperCase() + key.slice(1)])
        v = config['mobile' + key[0].toUpperCase() + key.slice(1)];
      else if (w <= 768 && config['tablet' + key[0].toUpperCase() + key.slice(1)])
        v = config['tablet' + key[0].toUpperCase() + key.slice(1)];
      else if (w <= 1024 && config['desktop' + key[0].toUpperCase() + key.slice(1)])
        v = config['desktop' + key[0].toUpperCase() + key.slice(1)];
      setVal(v);
    };
    const deb = debounce(calc, 100);
    calc();
    window.addEventListener('resize', deb);
    return () => window.removeEventListener('resize', deb);
  }, [responsive, config, key]);
  return responsive ? val : config[key];
};

const useIntersectionObserver = (ref, shouldObserve = false) => {
  const [isVisible, setIsVisible] = useState(!shouldObserve);

  useEffect(() => {
    if (!shouldObserve || !ref.current) {
      // If not observing, ensure it's visible immediately
      setIsVisible(true);
      return;
    }

    const observer = new IntersectionObserver(([entry]) => setIsVisible(entry.isIntersecting), { threshold: 0.1 });

    observer.observe(ref.current);
    return () => observer.disconnect();
  }, [ref, shouldObserve]);

  return isVisible;
};

const GradualBlur = props => {
  const containerRef = useRef(null);
  const [isHovered, setIsHovered] = useState(false);

  const config = useMemo(() => {
    const presetConfig = props.preset && GRADUAL_BLUR_PRESETS[props.preset] ? GRADUAL_BLUR_PRESETS[props.preset] : {};
    return mergeConfigs(DEFAULT_GRADUAL_BLUR_CONFIG, presetConfig, props);
  }, [props]);

  const responsiveHeight = useResponsiveDimension(config.responsive, config, 'height');
  const responsiveWidth = useResponsiveDimension(config.responsive, config, 'width');
  const isVisible = useIntersectionObserver(containerRef, config.animated === 'scroll');

  const blurDivs = useMemo(() => {
    const divs = [];
    const increment = 100 / config.divCount;
    const currentStrength =
      isHovered && config.hoverIntensity ? config.strength * config.hoverIntensity : config.strength;

    const curveFunc = GRADUAL_BLUR_CURVE_FUNCTIONS[config.curve] || GRADUAL_BLUR_CURVE_FUNCTIONS.linear;

    for (let i = 1; i <= config.divCount; i++) {
      let progress = i / config.divCount;
      progress = curveFunc(progress);

      let blurValue;
      if (config.exponential) {
        blurValue = Math.pow(2, progress * 4) * 0.0625 * currentStrength;
      } else {
        blurValue = 0.0625 * (progress * config.divCount + 1) * currentStrength;
      }
      const p1 = Math.round((increment * i - increment) * 10) / 10;
      const p2 = Math.round(increment * i * 10) / 10;
      const p3 = Math.round((increment * i + increment) * 10) / 10;
      const p4 = Math.round((increment * i + increment * 2) * 10) / 10;
      let gradient = `transparent ${p1}%, black ${p2}%`;
      if (p3 <= 100) gradient += `, black ${p3}%`;
      if (p4 <= 100) gradient += `, transparent ${p4}%`;

      const direction = getGradientDirection(config.position);

      const divStyle = {
        position: 'absolute',
        inset: '0',
        maskImage: `linear-gradient(${direction}, ${gradient})`,
        WebkitMaskImage: `linear-gradient(${direction}, ${gradient})`,
        backdropFilter: `blur(${blurValue.toFixed(3)}rem)`,
        WebkitBackdropFilter: `blur(${blurValue.toFixed(3)}rem)`,
        opacity: config.opacity,
        transition:
          config.animated && config.animated !== 'scroll'
            ? `backdrop-filter ${config.duration} ${config.easing}`
            : undefined
      };

      divs.push(<div key={i} style={divStyle} />);
    }

    return divs;
  }, [config, isHovered]);

  const containerStyle = useMemo(() => {
    const isVertical = ['top', 'bottom'].includes(config.position);
    const isHorizontal = ['left', 'right'].includes(config.position);
    const isPageTarget = config.target === 'page';

    const baseStyle = {
      position: isPageTarget ? 'fixed' : 'absolute',
      pointerEvents: config.hoverIntensity ? 'auto' : 'none',
      opacity: isVisible ? 1 : 0,
      transition: config.animated ? `opacity ${config.duration} ${config.easing}` : undefined,
      zIndex: isPageTarget ? config.zIndex + 100 : config.zIndex,
      ...config.style
    };

    if (isVertical) {
      baseStyle.height = responsiveHeight;
      baseStyle.width = responsiveWidth || '100%';
      baseStyle[config.position] = 0;
      baseStyle.left = 0;
      baseStyle.right = 0;
    } else if (isHorizontal) {
      baseStyle.width = responsiveWidth || responsiveHeight;
      baseStyle.height = '100%';
      baseStyle[config.position] = 0;
      baseStyle.top = 0;
      baseStyle.bottom = 0;
    }

    return baseStyle;
  }, [config, responsiveHeight, responsiveWidth, isVisible]);

  const { hoverIntensity, animated, onAnimationComplete, duration } = config;
  useEffect(() => {
    if (isVisible && animated === 'scroll' && onAnimationComplete) {
      const t = setTimeout(() => onAnimationComplete(), parseFloat(duration) * 1000);
      return () => clearTimeout(t);
    }
  }, [isVisible, animated, onAnimationComplete, duration]);

  return (
    <div
      ref={containerRef}
      className={`gradual-blur ${config.target === 'page' ? 'gradual-blur-page' : 'gradual-blur-parent'} ${config.className}`}
      style={containerStyle}
      onMouseEnter={hoverIntensity ? () => setIsHovered(true) : undefined}
      onMouseLeave={hoverIntensity ? () => setIsHovered(false) : undefined}
    >
      <div className="gradual-blur-inner relative w-full h-full">{blurDivs}</div>
    </div>
  );
};

