              </div>
              <div>
                <p className="text-sm text-slate-400 mb-1">Phone</p>
                <a href={`tel:${APP_CONFIG.HELP_PHONE}`} className="font-medium text-indigo-400 hover:underline">{APP_CONFIG.HELP_PHONE}</a>
              </div>
              <div>
                <p className="text-sm text-slate-400 mb-1">Email</p>
                <a href={`mailto:${APP_CONFIG.HELP_EMAIL}`} className="font-medium text-indigo-400 hover:underline">{APP_CONFIG.HELP_EMAIL}</a>
              </div>
            </Card>
          </div>
        )}

        {/* ABOUT VIEW */}
        {activeTab === "about" && (
          <div className="p-4 max-w-2xl mx-auto">
            <h2 className="text-2xl font-bold text-white drop-shadow-lg mb-6">About</h2>
            <Card theme={currentTheme} className="space-y-6 text-center">
              <div className="flex items-center justify-center gap-3">
