      {/* Bottom Navigation - PillNav Style */}
      {(() => {
        const navColors = getThemeNavColors(currentTheme);
        const pillNavItems = [
          { id: "menu", label: "Menu" },
          { id: "cart", label: "Cart", badge: cartCount },
          { id: "orders", label: "Orders" },
          { id: "profile", label: "Profile" },
          { id: "settings", label: "Settings" },
          { id: "help", label: "Help" },
