                    onClick={() => setMenuForm({...menuForm, isVeg: false})}
                    className={`flex-1 py-3 rounded-xl border font-medium transition-all ${menuForm.isVeg === false ? "bg-orange-500/30 border-orange-500/50 text-orange-300" : "border-slate-600/40 text-slate-400 hover:border-slate-500"}`}
                  >
                    Non-veg
